import json
from flask import Flask


with open('candidates.json', encoding='utf-8') as file:
    candidates = json.load(file)



app = Flask(__name__)

@app.route('/')
def page_index():
    to_return = []
    for candidate in candidates:
        to_return.append(f"{candidate['name']}\n" \
                         f"{candidate['id']}\n" \
                         f"{candidate['skills']}\n\n")
    return f"<pre>{''.join(to_return)}</pre>"

@app.route('/candidates/<int:cand_num>')
def page_per_num(cand_num):
    to_return = []
    to_return.append(f'<img src="data:image/jpeg;base64,/9j/4AAQSkZJRgAB ">\n\n')

    to_return.append(f"{candidates[cand_num-1]['name']}\n" \
                     f"{candidates[cand_num-1]['id']}\n" \
                     f"{candidates[cand_num-1]['skills']}")



    return f'<pre>{"".join(to_return)}</pre>'

@app.route('/skills/<skill>')
def page_per_skills(skill: str):
    to_return = []
    for candidate in candidates:
        skill_list = candidate['skills'].split(', ')
        for skill_ in skill_list:
            if skill.lower() == skill_.lower():
                to_return.append(f"{candidate['name']}\n" \
                                 f"{candidate['id']}\n" \
                                 f"{candidate['skills']}\n\n")
    if to_return:
        return f'<pre>{"".join(to_return)}</pre>'

    return "Not found"



app.run(host='127.0.0.2', port=80)